package com.es.api;public class ElasticSearchDocument<T> {    private String id;    private T data;    public String getId() {        return id;    }    public ElasticSearchDocument setId(String id) {        this.id = id;        return this;    }    public T getData() {        return data;    }    public ElasticSearchDocument setData(T data) {        this.data = data;        return this;    }}