package com.es.controller;import com.es.entity.User;import com.es.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import java.util.List;/** * @author <a href="mailto:xinput.xx@gmail.com">xinput</a> * @date 2020-11-13 13:18 */@RestController@RequestMapping("/users")public class UserController {    @Autowired    private UserService userService;    @PostMapping    public void save(@RequestBody User user) {        userService.save(user);    }    @PostMapping("/batch")    public void saveBatchUser(@RequestBody List<User> users) {        userService.saveAllUser(users);    }    @GetMapping("/{id}")    public User getUser(@PathVariable("id") String id) {        User user = userService.getUser(id);        return user;    }    @GetMapping("/all")    public List<User> searchUserByName(@RequestParam String name) {        return userService.searchUserByName(name);    }    @DeleteMapping("/{id}")    public void deleteUser(@PathVariable("id") String id) {        userService.deleteUser(id);    }    @DeleteMapping("/user/{name}")    public void deleteUserByName(@PathVariable("name") String name) {        userService.deleteUserByName(name);    }}