package com.es.exception;import org.springframework.http.HttpStatus;/** * @author <a href="mailto:xinput.xx@gmail.com">xinput</a> * @date 2020-11-13 11:07 */public abstract class BaseException extends RuntimeException {    public BaseException(String message) {        super(message);    }    public BaseException(String message, Throwable cause) {        super(message, cause);    }    protected abstract HttpStatus getStatus();}