package com.es.exception;import org.springframework.http.HttpStatus;/** * @author <a href="mailto:xinput.xx@gmail.com">xinput</a> * @date 2020-11-13 11:08 */public class NotFoundException extends BaseException {    public NotFoundException(String message) {        super(message);    }    public NotFoundException(String message, Throwable cause) {        super(message, cause);    }    @Override    protected HttpStatus getStatus() {        return HttpStatus.NOT_FOUND;    }}